import React, {
    Component
} from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class TodoApp extends Component {
    constructor (props) {
        super(props);
        this.state = {
            items: [],
            text: ''
        };
        this.changeHandle = this.changeHandle.bind(this);
        this.submitHandle = this.submitHandle.bind(this);
        this.deleteItem   = this.deleteItem.bind(this);
    }
    changeHandle (e) {
        this.setState({
            text: e.target.value
